//LIST OF ALL ATTENDANTS
import {
    Box,
    Button,
    DialogActions,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
} from '@mui/material';
import {makeStyles} from "@mui/styles";
import Controls from "../components/controls/Controls";
import React, {useEffect, useState} from 'react';
import AdminServices from "../service/AdminServices";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import BlackListServices from "../service/BlackListServices";
import AttendantService from "../service/AttendantService";

const useStyles = makeStyles({
    pageContent: {
        margin: "40px",
        padding: "24px",
    },
    buttondelete:{
        backgroundColor : "red",
    },

});

export default function AttendantPage(props) {
    const [attendants, setAttendants] = React.useState([]);
    const [searchLocation, setSearchLocation] = React.useState("");
    const [regNumber, setRegNumber]= React.useState("");
    const [currentAttendantsList, setCurrentAttendantsList] = useState(null);
    const [currentIndex, setCurrentIndex] = useState(-1);

    useEffect(() =>{
        retrieveAllAttendants();
    }, []);

    const onChangeSearchRegistrationNumber = e =>{
        const regNumber = e.target.value;
        setRegNumber(regNumber);
    }

    const retrieveAllAttendants = () =>{
        AdminServices.getAllAttendants().then(response => {
            setAttendants(response.data);
            console.log(response);
        }).catch(error => {
            console.log(error);
        })
    }





    const findAttendantByID = () =>{
        AdminServices.getAttendant(regNumber).then(response =>{
            setAttendants(response.data);
            console.log(response);
        }).catch(error => {
            console.log(error)
        })
    }

    const handleDelete = (email) => {
        AttendantService.removeAttendant(email).then(response =>{
            alert("attendant removed");
            refreshList();
            console.log(response);
        }).catch(error => {
            console.log(error)
        })
        setOpen(false);
    };

    const classes = useStyles();

    return (
        <>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 1,
                        width: '100%',
                        height: '100%',
                    },
                }}
            >
                <Paper elevation={3} className={classes.pageContent}>
                    <div>
                        <Controls.Input
                            variant="outlined"
                            label="Search by Reg Number"
                            value={regNumber}
                            onChange={onChangeSearchRegistrationNumber}
                        />
                        <Button variant="contained"
                                onClick={findAttendantByID}>Search</Button>
                    </div>
                    <div align="right">
                        <Button variant="contained"
                                onClick={retrieveAllAttendants}>GetAll</Button>
                    </div>
                    <TableContainer component={Paper}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                                <TableRow>

                                    <TableCell align="right">firstName</TableCell>
                                    <TableCell align="right">lastName</TableCell>
                                    <TableCell align="right">email</TableCell>
                                    <TableCell align="right">gender</TableCell>
                                    <TableCell align="right">areaAssigned</TableCell>
                                    <TableCell align="right">Edit</TableCell>
                                    <TableCell align="right">Action</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {attendants.map((row, index) => (
                                    <TableRow
                                        key={index}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell>{row.firstName}</TableCell>
                                        <TableCell align="right">{row.lastName}</TableCell>
                                        <TableCell align="right">{row.email}</TableCell>
                                        <TableCell align="right">{row.gender}</TableCell>
                                        <TableCell align="right">{row.areaAssigned}</TableCell>
                                        <TableCell align="right">
                                            <Button variant="contained"
                                                    fullWidth
                                                    onClick={viewDetails}
                                            >View Issue</Button>
                                            <Dialog
                                                open={open}
                                                onClose={handleClose}
                                                aria-labelledby="alert-dialog-title"
                                                aria-describedby="alert-dialog-description"
                                            >
                                                <DialogTitle id="alert-dialog-title">
                                                    {"Parking Charges"}
                                                </DialogTitle>
                                                <DialogContent>
                                                    <DialogContentText id="alert-dialog-description" className={classes.detailDisplay}>
                                                        {row.remarks}
                                                    </DialogContentText>
                                                    <DialogActions >
                                                        <Button variant="contained"
                                                                fullWidth
                                                                onClick={handleClose}
                                                        >RETAIN</Button>
                                                        <Button variant="contained"
                                                                fullWidth
                                                                className={classes.buttondelete}
                                                                onClick={() => handleDelete(row.numberPlate)}
                                                        >DELETE</Button>
                                                    </DialogActions>
                                                </DialogContent>
                                            </Dialog>
                                        </TableCell>
                                        <TableCell align="right"><Button variant="contained"
                                                                         fullWidth
                                                                         className={classes.buttondelete}
                                                                         onClick={() => handleDelete(row.email)}
                                        >DELETE</Button></TableCell>
                                    </TableRow>
                                ))
                                }

                            </TableBody>
                        </Table>
                    </TableContainer>


                </Paper>
            </Box>
        </>
    )
}
