import React, {useEffect, useState} from 'react'
import {makeStyles} from "@mui/styles";
import {Box, Button, Grid, Paper} from '@mui/material';
import Controls from "../components/controls/Controls";
import {Form} from "../components/UseForm";
import * as SockJS from 'sockjs-client';
import * as Stomp from 'stompjs';


const useStyles = makeStyles({
    pageContent: {
        margin: "40px",
        padding: "24px",
    },

});


const initialFieldValues = {
    type: "",
    content: "",
    sender: "",
};

function connection(event){
    var socket = new SockJS('http://localhost:8080/ws');
    let stompClient = Stomp.over(socket);

    stompClient.connect({}, onConnected)
}

export default function Support() {
    const [messageDetail, setMessageDetail] =useState(initialFieldValues);
    //const [stompClient, setStompClient] = useState();


    useEffect(() =>{
        connection();
    }, []);



    const onConnected = () =>{
        stompClient.subscribe('http://localhost:8080/topic/public', onMessageReceived);
        stompClient.send(
            "http://localhost:8080/app/chat.addUser",
            {},
            JSON.stringify({sender: "userA", type: 'JOIN'})
        )
    }

    const handleSubmitMessage = (event) =>{
        event.preventDefault();

        const data = {
            type: messageDetail.type,
            content: messageDetail.content,
            sender: messageDetail.sender,
        }

        stompClient.send("http://localhost:8080/app/chat.sendMessage", {}, JSON.stringify(data));

    };

    const onMessageReceived =(messagePayload) =>{
        var message = JSON.parse(messagePayload.body);
        if(message.type === 'JOIN') {
            message.content = message.sender + ' joined!';
        } else if (message.type === 'LEAVE') {
            message.content = message.sender + ' left!';
        }


        var messageText = document.createTextNode(message.content);
        return(
            <div>{messageText}</div>
        )
    }


    const handleInputChange = e =>{

        const {name, value} = e.target
        setMessageDetail({
            ...messageDetail,
            [name]: value,
        })
    }

    const classes = useStyles();
    return (
        <>
            <div>
                <h1>Support</h1>
            </div>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 1,
                        width: '100%',
                        height: '100%',
                    },
                }}
            >
                <Paper elevation={3} className={classes.pageContent}>
                   <ul>
                       {onMessageReceived}
                   </ul>

                    <Form onSubmit={handleSubmitMessage}>
                        <Grid container>
                            <Grid item xs={6}>
                                <Controls.Input
                                    variant="outlined"
                                    label="Type Message"
                                    name="content"
                                    value={messageDetail.content}
                                    onChange={handleInputChange}
                                />
                                <Button variant="contained"
                                        fullWidth
                                        onClick={handleSubmitMessage}
                                >SEND</Button>
                            </Grid>
                        </Grid>
                    </Form>
                </Paper>
            </Box>
        </>

    )
}
