import {useEffect, useState} from 'react';
import { Box, Button, Paper, Card, CardContent, Grid, CardHeader, Divider, TextField } from '@mui/material';
import DataService from "../service/DataService";
import AdminServices from "../service/AdminServices";
import * as React from 'react';
import CardActions from '@mui/material/CardActions';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Stack from '@mui/material/Stack';
import axios from "axios";
import AuthService from "../service/auth.service";
import {makeStyles} from "@mui/styles";


const useStyles = makeStyles({
    pageContent: {
        margin: "40px",
        padding: "24px",
    },

});


const initialFieldValues = {
    id: 0,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    password: ""

};


export default function AccountSettingsAdmin() {
    const [userDetails , setUserDetails] = useState(initialFieldValues);
    const [values, setValues] = useState({
        password: '',
        confirm: ''
    });


    useEffect(() =>{
        const user = AuthService.getCurrentUser();
/*
        const isAdmin = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ADMIN';
        const isAttendant = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ATTENDANT';
        const isMotorist = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_MOTORIST';
        const email = JSON.parse(atob(user.split('.')[1])).sub;

        //const isAdmin = JSON.parse(atob(user.split('.')[1])).role === 'ROLE_MOTORIST';

 */

        if(user){
            const isAdmin = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ADMIN';
            const email = JSON.parse(atob(user.split('.')[1])).sub;
            if(isAdmin){
                AdminServices.getAdmin(email).then(response => {
                    setUserDetails(response.data);
                    console.log(response);
                }).catch(error => {
                    console.log(error)
                })
            }
        }
    }, []);

  /*
    useEffect(() =>{
        retrieveUsersDetails();
    }, []);


//await axios.get(`http://localhost:8080/apiv1/admin/get/${emailData}`)
    const retrieveUsersDetails = async () =>{

        AdminServices.getAdmin(emailData).then(response => {
            setUserDetails(response.data);
            console.log(response);
        }).catch(error => {
            console.log(error)
        })

    }

   */

    const handleChange = (event) => {
        setValues({
            ...values,
            [event.target.name]: event.target.value
        });
    };

    const classes = useStyles();

    return (
        <>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 1,
                        width: '100%',
                        height: '100%',
                    },
                }}
            >
                <Paper elevation={3} className={classes.pageContent}>
            <Card sx={{ maxWidth: 370 }}>
                <CardHeader
                    subheader="Account Information"
                    title="Details"
                />
                <Divider />
                <CardContent>
                    <Stack direction="row" spacing={2}>
                        <Avatar>A</Avatar>
                    </Stack>
                    <Typography gutterBottom variant="h5" component="div">
                        {userDetails.firstName}
                    </Typography>

                    <Grid item xs={6}>
                    <Typography variant="body2" color="text.secondary">
                        First Name: {userDetails.firstName}<br/>
                        Last Name: {userDetails.lastName}<br/>
                        Gender: {userDetails.gender}<br/>
                        Email: {userDetails.email}
                    </Typography>
                    </Grid>
                </CardContent>
                <CardActions>
                    <Button size="small">Change Password</Button>
                </CardActions>
            </Card>
                </Paper>
            </Box>

        <form >
            <Card>
                <CardHeader
                    subheader="Update password"
                    title="Password"
                />
                <Divider />
                <CardContent>
                    <TextField
                        fullWidth
                        label="Password"
                        margin="normal"
                        name="password"
                        onChange={handleChange}
                        type="password"
                        value={values.password}
                        variant="outlined"
                    />
                    <TextField
                        fullWidth
                        label="Confirm password"
                        margin="normal"
                        name="confirm"
                        onChange={handleChange}
                        type="password"
                        value={values.confirm}
                        variant="outlined"
                    />
                </CardContent>
                <Divider />
                <Box
                    sx={{
                        display: 'flex',
                        justifyContent: 'flex-end',
                        p: 2
                    }}
                >
                    <Button
                        color="primary"
                        variant="contained"
                    >
                        Update
                    </Button>
                </Box>
            </Card>
        </form>
        </>
    );
};
