import { format } from 'date-fns';
import { v4 as uuid } from 'uuid';
import PerfectScrollbar from 'react-perfect-scrollbar';
import {
  Box,
  Button,
  Card,
  CardHeader,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TableSortLabel,
  Tooltip,
  Paper,
  Grid,

} from '@mui/material';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
import SeverityPill from '../components/severity-pill';
import { makeStyles } from "@mui/styles";
import { UseForm, Form } from "../components/UseForm";
import Controls from "../components/controls/Controls";
import Avatar from '@mui/material/Avatar';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import React, { useState, useEffect  } from 'react'
import axios from "axios";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import Stack from "@mui/material/Stack";
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";
import TextField from "@mui/material/TextField";
import LocalizationProvider from "@mui/lab/LocalizationProvider";

const useStyles = makeStyles({
  pageContent: {
    margin: "40px",
    padding: "24px",
  },

});


const orders = [
    {
      id: uuid(),
      ref: 'CDD1049',
      amount: 30.5,
      customer: {
        name: 'Ekaterina Tankova'
      },
      createdAt: 1555016400000,
      status: 'pending'
    },
    {
      id: uuid(),
      ref: 'CDD1048',
      amount: 25.1,
      customer: {
        name: 'Cao Yu'
      },
      createdAt: 1555016400000,
      status: 'delivered'
    },
    {
      id: uuid(),
      ref: 'CDD1047',
      amount: 10.99,
      customer: {
        name: 'Alexa Richardson'
      },
      createdAt: 1554930000000,
      status: 'refunded'
    },
    {
      id: uuid(),
      ref: 'CDD1046',
      amount: 96.43,
      customer: {
        name: 'Anje Keizer'
      },
      createdAt: 1554757200000,
      status: 'pending'
    },
    {
      id: uuid(),
      ref: 'CDD1045',
      amount: 32.54,
      customer: {
        name: 'Clarke Gillebert'
      },
      createdAt: 1554670800000,
      status: 'delivered'
    },
    {
      id: uuid(),
      ref: 'CDD1044',
      amount: 16.76,
      customer: {
        name: 'Adam Denisov'
      },
      createdAt: 1554670800000,
      status: 'delivered'
    }
  ];

  const initialFieldValues = {
    id: 0,
    numberPlate: "",
    vehicleType: "",
    location: "",
    parkingLot: "",
    parkingDate: "",
    parkTime: "",
    parkDuration: "",

  };

export default function Bookings(props) {
  const [parkDetail, setParkDetail] = useState(initialFieldValues);
  const [selected, setSelected] = React.useState("");
  const [datey, setDatey] = React.useState(new Date());


  const handleInputChange = e =>{

    const {name, value} = e.target
    setParkDetail({
      ...parkDetail,
      [name]: value,
    })

  }
   const handleOptionOne = e =>{
     //setSelected(e.target.value);
    /* const {value} = e.target
     setParkDetail({
       location: value
     })
     */
     const {name, value} = e.target
     setSelected(value)
     setParkDetail({
       ...parkDetail,
       [name]: value,
     })
   }
  const handleOptionTwo = e =>{

   /* const {value} = e.target
    setParkDetail({
      parkingLot: value
    })
    */
    const {name, value} = e.target
    setParkDetail({
      ...parkDetail,
      [name]: value,
    })
  }

  const handleDateChange = e =>{

    const {name, value} = e.target
    setDatey(value)
    setParkDetail({
      ...parkDetail,
      [name]: value,
    })
  }


  const handleSubmit = e =>{
    e.preventDefault()



    axios.post("http://localhost:8080/apiv1/parkingdetails/save", parkDetail)
        .then(response => {
          console.log(response)
        })
        .catch(error => {
          console.log(error)
        })

  }


  const algorithm = [
    "Searching Algorithm",
    "Sorting Algorithm",
    "Graph Algorithm",
  ];
  const language = ["C++", "Java", "Python", "C#"];
  const dataStructure = ["Arrays", "LinkedList", "Stack", "Queue"];


  let type = null;


  let options = null;


  if (selected === "Algorithm") {
    type = algorithm;
  } else if (selected === "Language") {
    type = language;
  } else if (selected === "Data Structure") {
    type = dataStructure;
  }


  if (type) {
    options = type.map((el) => (<MenuItem key={el} value={el}>{el}</MenuItem>));
  }







  const classes = useStyles();
 // const { values, setValues, resetForm, handleInputChange, errors, setErrors } = UseForm(initialFieldValues, false);

    return (
      <>
      <Box
      sx={{
        display: 'flex',
        flexWrap: 'wrap',
        '& > :not(style)': {
          m: 1,
          width: '100%',
          height: 350,
        },
      }}
    >
      <Paper elevation={3} className={classes.pageContent}>
        <Avatar /> {/*name*/}
      <Form onSubmit={handleSubmit}>
      <Grid container>
        <Grid item xs={6}>
          <Controls.Input
            variant="outlined"
            label="Number Plate"
            name="numberPlate"
            value={parkDetail.numberPlate}
            onChange={handleInputChange}
          />



          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">CarType</InputLabel>
            <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select-label"
                name="vehicleType"
                value={parkDetail.vehicleType}
                label="Vehicle Type"
                onChange={handleInputChange}
            >
              <MenuItem value='Saloon'>Saloon</MenuItem>
              <MenuItem value='Lorry'>Lorry</MenuItem>
              <MenuItem value='Trailer'>Trailer</MenuItem>
            </Select>
          </FormControl>















          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <Stack spacing={3}>
              <DesktopDatePicker
                  label="Date"
                  inputFormat="MM/dd/yyyy"
                  value={parkDetail.parkingDate}
                  name="parkingDate"
                  onChange={handleDateChange}
                  renderInput={(params) => <TextField {...params} />}
              />
            </Stack>
          </LocalizationProvider>

        </Grid>
        <Grid item xs={6}>




          <FormControl fullWidth>
          <InputLabel id="dependant-dropdown">Area</InputLabel>
          <Select
              labelId="dependant-dropdown"
              id="dependant-dropdown"
              name="location"
              value={parkDetail.location}
              label="Location"
              onChange={handleOptionOne}
          >
            <MenuItem value='Choose...'>Choose...</MenuItem>
            <MenuItem value='Algorithm'>Algorithm</MenuItem>
            <MenuItem value='Language'>Language</MenuItem>
            <MenuItem value='Data Structure'>Data Structure</MenuItem>
          </Select>
        </FormControl>
        <FormControl>
          <InputLabel id="dropdown">Park Lot</InputLabel>
          <Select
              labelId="dropdown"
              id="dropdown"
              name="parkingLot"
              value={parkDetail.parkingLot}
              label="Park Lot"
              onChange={handleOptionTwo}>{options}</Select>
        </FormControl>








          <Controls.Input
              variant="outlined"
              label="Park Duration"
              name="parkDuration"
              value={parkDetail.parkDuration}
              onChange={handleInputChange}
          />
          </Grid>
        </Grid>
        <Button variant="contained"
        fullWidth
                onClick={handleSubmit}
        >Submit</Button>
        </Form>
      </Paper>
    </Box>
        <Card {...props}>
    <CardHeader title="Latest Orders" />
    <PerfectScrollbar>
      <Box sx={{ minWidth: 800 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>
                Order Ref
              </TableCell>
              <TableCell>
                Customer
              </TableCell>
              <TableCell sortDirection="desc">
                <Tooltip
                  enterDelay={300}
                  title="Sort"
                >
                  <TableSortLabel
                    active
                    direction="desc"
                  >
                    Date
                  </TableSortLabel>
                </Tooltip>
              </TableCell>
              <TableCell>
                Status
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map((order) => (
              <TableRow
                hover
                key={order.id}
              >
                <TableCell>
                  {order.ref}
                </TableCell>
                <TableCell>
                  {order.customer.name}
                </TableCell>
                <TableCell>
                  {format(order.createdAt, 'dd/MM/yyyy')}
                </TableCell>
                <TableCell>
                  <SeverityPill
                    color={(order.status === 'delivered' && 'success')
                    || (order.status === 'refunded' && 'error')
                    || 'warning'}
                  >
                    {order.status}
                  </SeverityPill>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Box>
    </PerfectScrollbar>
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'flex-end',
        p: 2
      }}
    >
      <Button
        color="primary"
        endIcon={<ArrowRightIcon fontSize="small" />}
        size="small"
        variant="text"
      >
        View all
      </Button>
    </Box>
  </Card>
  </>
