export default function AttendantRegistration(){
    const [attendantDetail, setAttendantDetail] =useState(initialFieldValues);

    const handleInputChange = e =>{

        const {name, value} = e.target
        setAttendantDetail({
            ...attendantDetail,
            [name]: value,
        })
    }


    const handleSubmit = (event) =>{
        event.preventDefault();
        const data = {
            //id: motoristDetail.id,
            firstName: attendantDetail.firstName,
            lastName: attendantDetail.lastName,
            email: attendantDetail.email,
            gender: attendantDetail.gender,
            password: attendantDetail.password,
            areaAssigned: attendantDetail.areaAssigned,
        }

        AdminServices.registerAttendant(data).then(response =>{
            console.log(response);
        }).catch(error =>{
            console.log(error)
        });
    };

    const classes = useStyles();

    return(
        <>
            <Box
                sx={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    '& > :not(style)': {
                        m: 1,
                        width: '100%',
                        height: '100%',
                    },
                }}
            >
                <Paper elevation={3} className={classes.pageContent}>
                    <Avatar /> {/*name*/}
                    <Form onSubmit={handleSubmit}>
                        <Grid container>
                            <Grid item xs={6}>
                                <Controls.Input
                                    variant="outlined"
                                    label="First Name"
                                    name="firstName"
                                    value={attendantDetail.firstName}
                                    onChange={handleInputChange}
                                />
                                <Controls.Input
                                    variant="outlined"
                                    label="Last Name"
                                    name="lastName"
                                    value={attendantDetail.lastName}
                                    onChange={handleInputChange}
                                />
                                <Controls.Input
                                    variant="outlined"
                                    label="Email"
                                    name="email"
                                    value={attendantDetail.email}
                                    onChange={handleInputChange}
                                />

                            </Grid>
                            <Grid item xs={6}>
                                <Controls.RadioGroup
                                    variant="outlined"
                                    name="gender"
                                    label="Gender"
                                    color="primary"
                                    value={attendantDetail.gender}
                                    genderItems={genderItems}
                                    onChange={handleInputChange}
                                />

                                <Controls.Input
                                    variant="outlined"
                                    label="Area Assigned"
                                    name="areaAssigned"
                                    value={attendantDetail.areaAssigned}
                                    onChange={handleInputChange}
                                />

                                <TextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    name="password"
                                    label="Password"
                                    type="password"
                                    value={attendantDetail.password}
                                    onChange={handleInputChange}
                                    autoComplete="current-password"
                                />

                            </Grid>
                        </Grid>
                        <Button variant="contained"
                                fullWidth
                                onClick={handleSubmit}
                        >Submit</Button>
                    </Form>
                </Paper>
            </Box>
        </>
    )
}
