import {useEffect, useState} from 'react';
import { Box, Button, Card, CardContent, CardHeader, Divider, TextField } from '@mui/material';
import DataService from "../service/DataService";
import AdminServices from "../service/AdminServices";
import * as React from 'react';
import CardActions from '@mui/material/CardActions';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Stack from '@mui/material/Stack';
import axios from "axios";
import AuthService from "../service/auth.service";


function stringToColor(string) {
    let hash = 0;
    let i;

    /* eslint-disable no-bitwise */
    for (i = 0; i < string.length; i += 1) {
        hash = string.charCodeAt(i) + ((hash << 5) - hash);
    }

    let color = '#';

    for (i = 0; i < 3; i += 1) {
        const value = (hash >> (i * 8)) & 0xff;
        color += `00${value.toString(16)}`.substr(-2);
    }
    /* eslint-enable no-bitwise */

    return color;
}

function stringAvatar(name) {
    return {
        sx: {
            bgcolor: stringToColor(name),
        },
        children: `${name.split(' ')[0][0]}${name.split(' ')[1][0]}`,
    };
}


const initialFieldValues = {
    id: 0,
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    password: ""

};


export default function AccountSettingsAdmin() {
    const [userDetails , setUserDetails] = useState(initialFieldValues);
    const [values, setValues] = useState({
        password: '',
        confirm: ''
    });
    const name = userDetails.firstName;

    useEffect(() =>{
        const user = AuthService.getCurrentUser();
/*
        const isAdmin = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ADMIN';
        const isAttendant = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ATTENDANT';
        const isMotorist = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_MOTORIST';
        const email = JSON.parse(atob(user.split('.')[1])).sub;

        //const isAdmin = JSON.parse(atob(user.split('.')[1])).role === 'ROLE_MOTORIST';

 */

        if(user){
            const isAdmin = JSON.parse(atob(user.split('.')[1])).roles[0] === 'ROLE_ADMIN';
            const email = JSON.parse(atob(user.split('.')[1])).sub;
            if(isAdmin){
                AdminServices.getAdmin(email).then(response => {
                    setUserDetails(response.data);
                    console.log(response);
                }).catch(error => {
                    console.log(error)
                })
            }
        }
    }, []);

  /*
    useEffect(() =>{
        retrieveUsersDetails();
    }, []);


//await axios.get(`http://localhost:8080/apiv1/admin/get/${emailData}`)
    const retrieveUsersDetails = async () =>{

        AdminServices.getAdmin(emailData).then(response => {
            setUserDetails(response.data);
            console.log(response);
        }).catch(error => {
            console.log(error)
        })

    }

   */

    const handleChange = (event) => {
        setValues({
            ...values,
            [event.target.name]: event.target.value
        });
    };

    return (
        <>
            <Box
                sx={{
                    marginTop: 8,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
            >
            <Card sx={{ maxWidth: 345 }}>
                <CardHeader
                    subheader="Account Information"
                    title="Details"
                />
                <Divider />
                <CardContent>
                    <Stack direction="row" spacing={2}>
                        <Avatar {...stringAvatar('ADMIN')} />
                    </Stack>
                    <Typography gutterBottom variant="h5" component="div">

                    </Typography>
                    {userDetails.firstName}
                    <Typography variant="body2" color="text.secondary">
                        First Name: {userDetails.firstName}
                        Last Name: {userDetails.lastName}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small">Share</Button>
                    <Button size="small">Learn More</Button>
                </CardActions>
            </Card>
            </Box>
        <form >
            <Card>
                <CardHeader
                    subheader="Update password"
                    title="Password"
                />
                <Divider />
                <CardContent>
                    <TextField
                        fullWidth
                        label="Password"
                        margin="normal"
                        name="password"
                        onChange={handleChange}
                        type="password"
                        value={values.password}
                        variant="outlined"
                    />
                    <TextField
                        fullWidth
                        label="Confirm password"
                        margin="normal"
                        name="confirm"
                        onChange={handleChange}
                        type="password"
                        value={values.confirm}
                        variant="outlined"
                    />
                </CardContent>
                <Divider />
                <Box
                    sx={{
                        display: 'flex',
                        justifyContent: 'flex-end',
                        p: 2
                    }}
                >
                    <Button
                        color="primary"
                        variant="contained"
                    >
                        Update
                    </Button>
                </Box>
            </Card>
        </form>
        </>
    );
};
